export const initialState = {
  isLoggedIn: false,
  isLoggingIn: false, //로그인 시도중
  isLoggingOut: false, //로그아웃 시도중
  me: null,
  signUpData: {},
  loginData: {},
};

//action
export const loginRequestAction = (data) => {
  return {
    type: "LOG_IN_REQUEST",
    data,
  };
};

export const logoutRequestAction = () => {
  return {
    type: "LOG_OUT_REQUEST",
  };
};

//reducer
const reducer = (state = initialState, action) => {
  switch (action.type) {
    case "LOG_IN_REQUEST":
      return {
        ...state,
        isLoggingIn: true,
      };
    case "LOG_IN_SUCCESS":
      return {
        ...state,
        me: { ...action.data, nickname: "chee9" },
        isLoggedIn: true,
        isLoggingIn: false,
      };
    case "LOG_IN_FAILURE":
      return {
        ...state,
        isLoggingIn: false,
      };
    case "LOG_OUT_REQUEST":
      return {
        ...state,
        isLoggingOut: true,
      };
    case "LOG_OUT_SUCCESS":
      return {
        ...state,
        me: null,
        isLoggedIn: false,
        isLoggingOut: false,
      };
    case "LOG_OUT_FAILURE":
      return {
        ...state,
        isLoggingOut: false,
      };

    default:
      return state;
  }
};

export default reducer;
