export const initialState = {
  mainPosts: [
    {
      id: 1,
      User: {
        id: 1,
        nickname: "ㅈㅔ로초",
      },
      content: "첫번째 게시글 # 해시태그",
      Images: [{}],
      Comments: [
        {
          User: {
            nickname: "nero",
          },
          content: "우와~ ",
        },
      ], //sequelize 와 연관되어 있기 때문에 (다른정보와 합쳐줘서 대문자로 줌)
    },
  ],
};
//action
const ADD_POST = "ADD_POST"; //상수로 빼주면 재활용할 수 있다.
export const addPost = {
  type: ADD_POST,
  data,
};
const dummyPost = {
  id: 2,
  content: "더미",
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_POST:
      return {
        ...state,
        mainPost: [dummyPost, ...state.mainPosts],
      };
    default:
      return state;
  }
};

export default reducer;
