import { HYDRATE } from "next-redux-wrapper";

const initialState = {
  user: {
    isLoggedIn: false,
    user: null,
  },
  post: {
    mainPosts: [],
  },
};

//action
export const loginAction = (data) => {
  return {
    type: "LOG_IN",
    data,
  };
};

export const logoutAction = () => {
  return {
    type: "LOG_OUT",
  };
};

// (이전상태, 액션) => 다음상태
const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case HYDRATE:
      console.log("HYDRATE", action);
      return { ...state, ...action.payload };
    case "LOG_IN":
      return {
        ...state,
        user: {
          ...state.user,
          isLoggedIn: true,
        },
      };
    case "LOG_OUT":
      return {
        ...state,
        user: {
          user: null,
          isLoggedIn: false,
        },
      };
    default:
      return state;
  }
}; // reducer 는 함수

export default rootReducer;
