/* eslint-disable no-case-declarations */
import shortId from "shortid";
export const initialState = {
  mainPosts: [
    {
      id: 1,
      User: {
        id: 1,
        nickname: "체리슈",
      },
      content: "첫번째 게시글 #해시태그 ##태그당 #ㅎㅇ",
      Images: [
        {
          id: shortId.generate(),
          src:
            "https://cgeimage.commutil.kr/phpwas/restmb_allidxmake.php?idx=3&simg=2019043021255500507482b2d760618310114012.jpg",
        },
        {
          id: shortId.generate(),
          src:
            "http://ph.spotvnews.co.kr/news/photo/202001/338010_421317_1105.jpg",
        },
        {
          id: shortId.generate(),
          src:
            "http://ph.spotvnews.co.kr/news/photo/202001/338010_421317_1105.jpg",
        },
      ],
      Comments: [
        {
          id: shortId.generate(),
          User: {
            id: shortId.generate(),
            nickname: "nero",
          },
          content: "잘생겼다",
        },
      ], //sequelize 와 연관되어 있기 때문에 (다른정보와 합쳐줘서 대문자로 줌)
    },
  ],
  imagePaths: [],
  addPostLoading: false,
  addPostDone: false,
  addPostError: null,

  addCommentLoading: false,
  addCommentDone: false,
  addCommentError: null,
};
// action //상수로 빼주면 재활용할 수 있다.
export const ADD_POST_REQUEST = "ADD_POST_REQUEST";
export const ADD_POST_SUCCESS = "ADD_POST_SUCCESS";
export const ADD_POST_FAILURE = "ADD_POST_FAILURE";

export const ADD_COMMENT_REQUEST = "ADD_COMMENT_REQUEST";
export const ADD_COMMENT_SUCCESS = "ADD_COMMENT_SUCCESS";
export const ADD_COMMENT_FAILURE = "ADD_COMMENT_FAILURE";

export const DELETE_POST_REQUEST = "DELETE_POST_REQUEST";
export const DELETE_POST_SUCCESS = "DELETE_POST_SUCCESS";

export const addPost = (data) => {
  return {
    type: ADD_POST_REQUEST,
    data,
  };
};
export const addComment = (data) => {
  return {
    type: ADD_COMMENT_REQUEST,
    data,
  };
};
// 동적인 액션이 필요할 때 액션 크리에이터 함수를 쓴다
const dummyPost = (data) => ({
  id: shortId.generate(),
  content: data,
  User: {
    id: 1,
    nickname: "chee99",
  },
  Images: [],
  Comments: [],
});
const dummyComment = (data) => ({
  id: shortId.generate(),
  content: data,
  User: {
    id: 1,
    nickname: "chee99",
  },
});

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_POST_REQUEST:
      return {
        ...state,
        // mainPosts: [dummyPost, ...state.mainPosts], // 앞에 추가
        addPostDone: false,
        addPostLoading: true,
      };
    case ADD_POST_SUCCESS:
      return {
        ...state,
        addPostDone: true,
        addPostLoading: false,
        addPostError: null,
        mainPosts: [dummyPost(action.data), ...state.mainPosts],
      };
    case ADD_POST_FAILURE:
      return {
        ...state,
        addPostDone: true,
        addPostLoading: false,
        addPostError: action.data,
      };
    case ADD_COMMENT_REQUEST:
      return {
        ...state,
        // mainPosts: [dummyPost, ...state.mainPosts], // 앞에 추가
        addCommentDone: false,
        addCommentLoading: true,
      };

    case ADD_COMMENT_SUCCESS: {
      const postIndex = state.mainPosts.findIndex(
        (v) => v.id === action.data.postId,
      );
      const post = { ...state.mainPosts[postIndex] };
      post.Comments = [dummyComment(action.data.content), ...post.Comments];
      const mainPosts = [...state.mainPosts];
      mainPosts[postIndex] = post;
      return {
        ...state,
        mainPosts,
        addCommentDone: true,
        addCommentLoading: false,
        addCommentError: null,
      };
    }
    case ADD_COMMENT_FAILURE:
      return {
        ...state,
        addCommentDone: true,
        addCommentLoading: false,
        addCommentError: action.data,
      };
    default:
      return state;
  }
};

export default reducer;
